window.onload=()=>{console.log("loaded");const e=document.querySelector(".contacts__spoiler-btn"),t=document.querySelector(".header__contacts"),o=document.querySelector(".header__sup"),c=window.getComputedStyle(o,null).getPropertyValue("height");e.onclick=()=>{e.classList.toggle("contacts__spoiler-btn_active"),t.classList.contains("contacts_visible")?o.style.height=c:o.style.height=+c.replace("px","")+t.scrollHeight+"px"};const l=document.querySelector(".navbar__burger-btn");document.querySelector(".navbar__nav");l.onclick=()=>{l.classList.toggle("navbar__burger-btn_active"),document.body.classList.toggle("no-overflow")}};